import React from 'react';
import { useStore } from './store';

export const SubmitButton = () => {
  const { nodes, edges } = useStore((state) => ({
    nodes: state.nodes,
    edges: state.edges,
  }));

  const handleSubmit = async () => {
    const nodesParam = encodeURIComponent(JSON.stringify(nodes));
    const edgesParam = encodeURIComponent(JSON.stringify(edges));

    try {
      const response = await fetch(`http://127.0.0.1:8000/pipelines/parse?nodes=${nodesParam}&edges=${edgesParam}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const result = await response.json();
      console.log('Pipeline parsed:', result);
    } catch (error) {
      console.error('Error submitting pipeline:', error);
    }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <button type="button" onClick={handleSubmit}>
        Submit
      </button>
    </div>
  );
};
