import { useStore } from './store'; // Import the store to access nodes and edges
import { shallow } from 'zustand/shallow';

export const SubmitButton = () => {
    const { nodes, edges } = useStore((state) => ({
        nodes: state.nodes,
        edges: state.edges,
    }), shallow);

    const handleSubmit = async () => {
        const pipelineData = {
            nodes,
            edges
        };

        try {
            const response = await fetch('http://http://127.0.0.1:8000//pipelines/parse', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(pipelineData),
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Pipeline successfully submitted:', data);
            } else {
                console.error('Failed to submit pipeline:', response.statusText);
            }
        } catch (error) {
            console.error('Error submitting pipeline:', error);
        }
    };

    return (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            <button type="button" onClick={handleSubmit}>Submit</button>
        </div>
    );
};
